mapper.select:
select * from Inquiry
where member_id = #{member_id}
order by inquiry_date desc

select * from Inquiry
where seller_no = #{seller_no}
order by inquiry_date desc

mapper.update:
update Inquiry 
set inquiry_content = #{inquiry_content}
where inquiry_no = #{inquiry_no}

update Inquiry 
set inquiry_reply = #{inquiry_reply}, inquiry_replydate = SYSDATE 
where inquiry_no = #{inquiry_no}
