mapper.insert:
insert into orderproduct (
	member_id, product_no, order_product_amount, order_delivery_price, order_total_price, product_price, product_name
)
select #{member_id}, #{product_no}, #{order_product_amount},#{order_delivery_price},#{order_total_price}, product_price, product_name
	from product where product_no = #{product_no}
